import { Link } from 'react-router-dom'
import './CartAll.scss';
import { connect } from "react-redux";
import React, { useEffect, useState } from 'react';
import { getCartFromDB } from '../../utils/userUtils';
import { getAuctionFromDB } from '../../store/actions/currentAuction'
import de from '../../img/icons/dustbin.png'
import IconButton from '@material-ui/core/IconButton';
import OneAuction from '../homePage/OneAuction';
import { setNewAuction } from '../../store/actions/newAuction';
import { setCurrentAuction } from '../../store/actions/currentAuction';
import { signIn } from '../../store/actions/signIn';
import { setUserByStorage, setShowDeleteAuctionFromCartModal } from '../../store/actions/user';
import DeleteAuctionFromCartModal from './DeleteAuctionFromCartModal';

import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import useMediaQuery from '@material-ui/core/useMediaQuery';
import { useTheme } from '@material-ui/core/styles';

const CartAll = (props) => {

    useEffect(() => {
        let id = localStorage.getItem("user");
        if (id && props.currentUser == null) props.setUserByStorage(id)
    }, [])

    useEffect(() => {
        if (props.currentUser) getCartFromDB(props.currentUser._id).then(succ => {
            if (succ.status != 400) setArray(succ.data);
        })
    }, [props.currentUser])

    const [array, setArray] = React.useState(null);

    const DeleteAuctionFromCartModal = (props) => {
        const theme = useTheme();
        const fullScreen = useMediaQuery(theme.breakpoints.down('sm'));
    
    
        <Dialog
            fullScreen={fullScreen}
            open={true}
            onClick={() => props.setShowDeleteAuctionFromCartModal(false)}
            onClose={() => props.setShowDeleteAuctionFromCartModal(false)}
            aria-labelledby="responsive-dialog-title"
        >
            <DialogTitle id="responsive-dialog-title">{"Delete"}</DialogTitle>
            <DialogContent>
                <DialogContentText>Are you sure you want to delete {props.auctionName} shopping cart?</DialogContentText>
            </DialogContent>
            <DialogActions>
                <Button onClick={() => emptyTheCartByAuction(props.auctionId, props.userId).then(succ => {
                    if (succ.status != 400) getCartFromDB(props.userId).then(succ => {
                        if (succ.status != 400) setArray(succ.data);
                    })
                })} color="primary" autoFocus>
                    Ok
                </Button>
                <Button autoFocus color="primary">
                    Cancle
                </Button>
    
            </DialogActions>
        </Dialog>
    }
    
    
    return (<>
        {props.show && <DeleteAuctionFromCartModal />}
        <div id="a-container">
            {array && array.map((item) => {
                return (
                    <div> <Link
                        key={parseInt(item.auction._id)}
                        onClick={() => props.getAuctionFromDB(item.auction._id)}
                        to={`/auction`}>
                        <OneAuction key={parseInt(item.auction._id)} item={item.auction} />

                    </Link>
                        <center>
                            <h3>Amount to pay: {item.sum}â‚ª</h3>
                            <IconButton title="Delete" onClick={() => props.setShowDeleteAuctionFromCartModal(true)} >
                                <img className="my_icon" src={de} ></img>
                            </IconButton>
                        </center>
                    </div>
                )
            })}
        </div></>
    );
}

const mapStateToProps = state => {
    return {
        user: state.user,
        currentAuction: state.currentAuction.currentAuction,
        currentUser: state.user.currentUser,
        show: state.user.showDeleteAuctionFromCartModal
    }
}
export default connect(mapStateToProps, { signIn, setCurrentAuction, setNewAuction, getAuctionFromDB, setUserByStorage, setShowDeleteAuctionFromCartModal })(CartAll);
